import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AttendanceDashboard = () => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
    fetchRecords();
  }, []);

  const fetchRecords = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      if (!token) {
        setError('No authentication token found. Please log in again.');
        setLoading(false);
        return;
      }

      console.log('Fetching attendance records...');
      const response = await axios.get('http://localhost:5000/api/attendance', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      console.log('Records fetched:', response.data);
      setRecords(response.data.data);
      setError('');
    } catch (error) {
      console.error('Error fetching records:', error);
      const errorMsg = error.response?.data?.error || 'Failed to fetch records. Please try again.';
      setError(errorMsg);
      setRecords([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this record?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/api/attendance/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        // Refresh the list after deletion
        fetchRecords();
      } catch (error) {
        console.error('Error deleting record:', error);
        setError('Failed to delete record');
      }
    }
  };

  if (loading) {
    return (
      <div className="dashboard">
        <h2>My Attendance Records</h2>
        <div className="loading">Loading your attendance records...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard">
        <h2>My Attendance Records</h2>
        <div className="error-message">
          {error}
          <button onClick={fetchRecords} className="retry-btn" style={{marginLeft: '10px'}}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h2>My Attendance Records</h2>
      
      {user && (
        <div className="user-info-card">
          <h3>Employee Information</h3>
          <p><strong>Name:</strong> {user.employeeName}</p>
          <p><strong>ID:</strong> {user.employeeID}</p>
          <p><strong>Department:</strong> {user.department || 'Not specified'}</p>
          <p><strong>Position:</strong> {user.position || 'Not specified'}</p>
        </div>
      )}

      <div className="records-count">
        Total Records: {records.length}
      </div>
      
      {records.length === 0 ? (
        <div className="no-records">
          <p>No attendance records found.</p>
          <p>Start by marking your attendance using the "Mark Attendance" tab.</p>
        </div>
      ) : (
        <div className="records-table-container">
          <table className="records-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Status</th>
                <th>Recorded At</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {records.map(record => (
                <tr key={record.id}>
                  <td>{new Date(record.date).toLocaleDateString()}</td>
                  <td>
                    <span className={`status ${record.status.toLowerCase()}`}>
                      {record.status}
                    </span>
                  </td>
                  <td>{new Date(record.createdAt).toLocaleString()}</td>
                  <td>
                    <button 
                      className="delete-btn"
                      onClick={() => handleDelete(record.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AttendanceDashboard;
